mapper.select:
SELECT COUNT(DISTINCT a.id)

FROM tb_article a
LEFT JOIN tb_article_tag at ON a.id = at.id

    is_delete = #{conditionVO.isDelete}

        and article_title like concat('%',#{conditionVO.keywords},'%')


        and `status` = #{conditionVO.status}


        and category_id = #{conditionVO.categoryId}


        and type = #{conditionVO.type}


        and at.tag_id = #{conditionVO.tagId}

SELECT a.id, article_cover AS articleCover, article_title AS articleTitle, a.create_time AS createTime, category_name AS categoryName, is_top AS isTop, is_featured AS isFeatured, is_delete AS isDelete, status, type FROM( SELECT id, category_id, article_cover, article_title, status, type, is_top, is_featured, is_delete, create_time FROM tb_article
is_delete = #{conditionVO.isDelete}
and article_title like concat('%',#{conditionVO.keywords},'%')
and status = #{conditionVO.status}
and category_id = #{conditionVO.categoryId}
and type = #{conditionVO.type}
and id in ( SELECT id FROM tb_article_tag WHERE tag_id = #{conditionVO.tagId} )
ORDER BY is_top DESC, is_featured DESC, id DESC ) a LEFT JOIN tb_category c ON a.category_id = c.id LEFT JOIN tb_article_tag at ON a.id = at.article_id LEFT JOIN tb_tag t ON t.id = at.tag_id ORDER BY is_top DESC, is_featured DESC, a.id DESC
