mapper.select:
SELECT COUNT(*) FROM tb_comment c LEFT JOIN tb_user_info ui ON c.user_id = ui.id
c.type = #{conditionVO.type}
AND c.is_review = #{conditionVO.isReview}
AND ui.nick_name like CONCAT('%',#{conditionVO.keywords},'%')

SELECT c.id AS id, ui.avatar AS avatar, ui.nick_name AS nickname, r.nick_name AS replyNickname, a.title AS articleTitle, c.comment_content AS commentContent, c.type AS type, c.is_review AS isReview, c.create_time AS createTime FROM tb_comment c LEFT JOIN tb_article a ON c.topic_id = a.id LEFT JOIN tb_user_info ui ON c.user_id = ui.id LEFT JOIN tb_user_info r ON c.reply_user_id = r.id
c.type = #{conditionVO.type}
AND c.is_review = #{conditionVO.isReview}
AND ui.nick_name like CONCAT('%',#{conditionVO.keywords},'%')
ORDER BY id DESC
