mapper.select:
SELECT COUNT(DISTINCT a.article_id)

FROM tb_article a
LEFT JOIN tb_article_tag at ON a.article_id = at.article_id

    is_delete = #{conditionVO.isDelete}

        and title like concat('%',#{conditionVO.keywords},'%')


        and `status` = #{conditionVO.status}


        and category_id = #{conditionVO.categoryId}


        and type = #{conditionVO.type}


        and at.tag_id = #{conditionVO.tagId}

SELECT a.article_id, cover AS article_cover, title AS article_title, is_top, is_featured, is_delete, status, type, a.create_time, category_name, t.tag_id, tag_name, FROM( SELECT article_id, cover, title, status, type, is_top, is_featured, is_delete, create_time, update_time, FROM tb_article
is_delete = #{conditionVO.isDelete}
and title like concat('%',#{conditionVO.keywords},'%')
and status = #{conditionVO.status}
and category_id = #{conditionVO.categoryId}
and type = #{conditionVO.type}
and id in ( SELECT article_id FROM tb_article_tag WHERE tag_id = #{conditionVO.tagId} )
ORDER BY is_top DESC, is_featured DESC, id DESC ) a LEFT JOIN tb_category c ON a.category_id = c.category_id LEFT JOIN tb_article_tag at ON a.article_id = at.article_id LEFT JOIN tb_tag t ON t.id = at.tag_id ORDER BY is_top DESC, is_featured DESC, a.id DESC
