mapper.select:
SELECT * FROM ( SELECT c.user_id AS userId, ui.nick_name AS nickname, ui.avatar AS avatar, ui.website AS website, c.reply_user_id AS replyUserId, r.nick_name AS replyNickname, r.website AS replyWebsite, c.id AS id, c.parent_id AS parentId, c.comment_content AS commentContent, c.create_time AS createTime FROM tb_comment c JOIN tb_user_info ui ON c.user_id = ui.id JOIN tb_user_info r ON c.reply_user_id = r.id WHERE c.is_review = 1 AND parent_id IN(
#{commentId}
) ORDER BY c.create_time DESC ) t

SELECT COUNT(*) FROM tb_comment c LEFT JOIN tb_user_info ui ON c.user_id = ui.id
c.type = #{conditionVO.type}
AND c.is_review = #{conditionVO.isReview}
AND ui.nick_name like CONCAT('%',#{conditionVO.keywords},'%')

SELECT c.id AS id, ui.avatar AS avatar, ui.nick_name AS nickname, r.nick_name AS replyNickname, a.title AS articleTitle, c.comment_content AS commentContent, c.type AS type, c.is_review AS isReview, c.create_time AS createTime, FROM tb_comment c LEFT JOIN tb_article a ON c.topic_id = a.article_id LEFT JOIN tb_user_info ui ON c.user_id = ui.id LEFT JOIN tb_user_info r ON c.reply_user_id = r.id
c.type = #{conditionVO.type}
AND c.is_review = #{conditionVO.isReview}
AND ui.nick_name like CONCAT('%',#{conditionVO.keywords},'%')
ORDER BY id DESC

SELECT topic_id AS id, COUNT(1) AS commentCount FROM tb_comment c WHERE type = #{type} AND topic_id IN
#{topicId}
GROUP BY topic_id

SELECT topic_id AS id,
       COUNT(1) AS commentCount
FROM tb_comment c
WHERE type = #{type}
  AND topic_id = #{topicId}
GROUP BY topic_id
